import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { makeStyles } from '@material-ui/core/styles';

import { setItems } from '../../actions/items';
import ItemTemplate from '../templates/itemTemplate';

const useStyles = makeStyles(theme => ({
    container: {
        display: "flex",
        flexDirection: "row",
        flexWrap: "wrap",
        marginLeft: "20%",
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(3, 0),
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
        marginLeft: -10,
    },
}));

function Home(props) {

    const classes = useStyles();

    const [itemCard, setItemCard] = useState(null);

    useEffect(() => {
        if (props.cart) {
            const itemList = props.cart.items.map((item) => {
                return (
                    <ItemTemplate
                        key={item.id}
                        title={item.title}
                        price={item.price}
                        image={item.image}
                        id={item.id}
                    />

                )
            });
            setItemCard(itemList);
        }
    }, [props.items]);

    useEffect(() => {
        console.log(props)
    })

    return (
        <main>

            <div className={classes.drawerHeader} />

            <div className={classes.container}>{itemCard}</div>

        </main>
    )
}

// Home.defaultProps = {
//     cart: null,
// }

Home.propTypes = {
    setItemsData: PropTypes.func.isRequired,
}

const mapStateToProps = store => ({
    cart: store.cart,
});

const mapDispatchToProps = dispatch => ({
    setItemsData: data => dispatch(setItems(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
