import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';
import IconButton from '@material-ui/core/IconButton';

import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import HighlightOffIcon from '@material-ui/icons/HighlightOff';

import { addToCart } from '../../actions/items';

const useStyles = makeStyles({
    card: {
        maxWidth: 800,
        marginBottom: 20,
        marginLeft: 20,
    },
    media: {
        height: 300,
        width: 200,
        maxWidth: 200,
        minWidth: 200
    },
    button: {
        display: "flex",
        flexDirection: "row",
        justifyContent: "center"
    },
    iconButton: {
        marginLeft: 20,
        padding: 0
        // paddingLeft: 3,
        // paddingRight: 3
    }
});

function ItemCartTemplate(props) {

    const classes = useStyles();

    const handleClick = () => {
        props.addToCartData(props.id);
    }

    return (
        <div>
            <Card className={classes.card}>
                <Box display="flex" flexDirection="row">
                    <CardMedia
                        className={classes.media}
                        image={props.image ? props.image : ''}
                    />
                    <CardContent>
                        <Typography gutterBottom variant="h5" component="h6">
                            {props.title ? props.title : ''}
                        </Typography>

                        <Box display="flex" flexDirection="row">
                            <Typography gutterBottom variant="h6" component="h6">
                                Количество: {props.quantity ? props.quantity : ''}
                            </Typography>

                            <IconButton aria-label="sub" size="small" className={classes.iconButton}>
                                <RemoveIcon fontSize="inherit" />
                            </IconButton>

                            <IconButton aria-label="add" size="small" className={classes.iconButton}>
                                <AddIcon fontSize="inherit" />
                            </IconButton>

                        </Box>


                        <Typography gutterBottom variant="h6" component="h6">
                            Цена: {(props.price && props.quantity) ? props.price * props.quantity : ''} грн
                            </Typography>
                    </CardContent>
                </Box>
            </Card>
        </div>
    );

}

ItemCartTemplate.defaultProps = {
    cart: null,
}

ItemCartTemplate.propTypes = {
    addToCartData: PropTypes.func.isRequired,
}

const mapStateToProps = store => ({
    cart: store.cart,
});

const mapDispatchToProps = dispatch => ({
    addToCartData: data => dispatch(addToCart(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ItemCartTemplate);
