import React, { useEffect, useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

import { makeStyles } from '@material-ui/core/styles';

import { setItems } from '../../actions/items';

import ItemCartTemplate from '../templates/itemCartTemplate'
import { Typography } from '@material-ui/core';

const useStyles = makeStyles(theme => ({
    container: {
        display: "flex",
        flexDirection: "column",
        marginLeft: "5%",
        marginRight: "5%",
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(3, 0),
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    totalPrice: {
        color: "grey",
        textAlign: "center"
    }
}));

function Cart(props) {

    const classes = useStyles();

    const [itemCard, setItemCard] = useState(null);
    const [cartContent, setCartContent] = useState(null);

    useEffect(() => {
        const cart = JSON.parse(localStorage.getItem('cart'));
        setCartContent(cart);
    }, [])

    useEffect(() => {
        if (cartContent) {
            const itemList = cartContent.itemsInCart.map((item) => {
                return (
                    <ItemCartTemplate
                        key={item.id}
                        title={item.title}
                        price={item.price}
                        image={item.image}
                        id={item.id}
                        quantity={item.quantity}
                    />

                )
            });
            setItemCard(itemList);
        }
    }, [cartContent]);

    return (
        <main>

            <div className={classes.drawerHeader} />

            <div className={classes.container}>{itemCard}</div>

            <Typography gutterBottom variant="h5" component="h6" className={classes.totalPrice}>
                К оплате: { cartContent ? cartContent.totalPrice : '' }
            </Typography>
        </main>
    )
}

Cart.defaultProps = {
    cart: null,
}

Cart.propTypes = {
    setItemsData: PropTypes.func.isRequired,
}

const mapStateToProps = store => ({
    cart: store.cart,
});

const mapDispatchToProps = dispatch => ({
    setItemsData: data => dispatch(setItems(data)),
});

export default connect(mapStateToProps, mapDispatchToProps)(Cart);
