}

Home.defaultProps = {
    items: null,
}

Home.propTypes = {
    setUserData: PropTypes.func.isRequired,
}

const mapStateToProps = store => ({
    items: store.items,
});

const mapDispatchToProps = dispatch => ({
    setUserData: data => dispatch(setUser(data)),
})

export default connect(mapStateToProps, mapDispatchToProps)(Home);
