import {
    SET_ITEMS, ADD_TO_CART
} from '../constants/item';

import image1 from '../images/image1.jpg';
import image2 from '../images/image2.jpg';
import image3 from '../images/image3.jpg';
import image4 from '../images/image4.jpg';
import image5 from '../images/image5.jpg';
import image6 from '../images/image6.jpg';
import image7 from '../images/image7.jpg';
import image8 from '../images/image8.jpg';
import image9 from '../images/image9.jpg';
import image10 from '../images/image10.jpg';

const initialState = {
    items: [
        { id: 1, title: 'Белое платье-миди с цельнокроеным рукавом', price: 895, image: image1, dateAddTOCart: '' },
        { id: 2, title: 'Платье-гольф черного цвета с потайной молнией', price: 695, image: image2, dateAddTOCart: '' },
        { id: 3, title: 'Обтягивающее платье бежевого цвета из стрейч-гипюра', price: 695, image: image3, dateAddTOCart: '' },
        { id: 4, title: 'Платье в серую клетку из костюмки с рукавами реглан ', price: 695, image: image4, dateAddTOCart: '' },
        { id: 5, title: 'Платье в тёмно-синюю клетку с расклешенной юбкой ', price: 795, image: image5, dateAddTOCart: '' },
        { id: 6, title: 'Синее платье с верхом из креп-шифона и принтованной юбкой', price: 695, image: image6, dateAddTOCart: '' },
        { id: 7, title: 'Серое платье свободного кроя с карманами ', price: 595, image: image7, dateAddTOCart: '' },
        { id: 8, title: 'Обтягивающее платье-миди пудрового цвета', price: 695, image: image8, dateAddTOCart: '' },
        { id: 9, title: 'Шифоновое платье с принтом и воротником-аскот зеленое', price: 795, image: image9, dateAddTOCart: '' },
        { id: 10, title: 'Платье с запахом чёрного цвета из креп-дайвинга', price: 675, image: image10, dateAddTOCart: '' },
    ],
    addedItems: [],
    total: 0
};

const cartReducer = (state = initialState, action) => {
    if(action.type === ADD_TO_CART){
        let addedItem = state.items.find(item=> item.id === action.id)
        //check if the action id exists in the addedItems
       let existed_item= state.addedItems.find(item=> action.id === item.id)
       if(existed_item)
       {
          addedItem.quantity += 1 
           return{
              ...state,
               total: state.total + addedItem.price 
                }
      }
       else{
          addedItem.quantity = 1;
          //calculating the total
          let newTotal = state.total + addedItem.price 

          return{
              ...state,
              addedItems: [...state.addedItems, addedItem],
              total : newTotal
          }

      }
  }
  else{
      return state
  }
}

export default cartReducer;
