import React, { useState, useEffect } from 'react';

import { makeStyles } from '@material-ui/core/styles';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Typography from '@material-ui/core/Typography';
import IconButton from '@material-ui/core/IconButton';
import Tooltip from '@material-ui/core/Tooltip';
import Badge from '@material-ui/core/Badge';

import ShoppingCartIcon from '@material-ui/icons/ShoppingCart';
import HomeIcon from '@material-ui/icons/Home';

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        display: 'flex',
    },
    title: {
        paddingRight: 10,
    },
    username: {
        color: 'white',
        marginRight: 20,
    },
    growElement: {
        flexGrow: 1,
    },
    drawerHeader: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(0, 1),
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',
    },
    margin: {
        margin: theme.spacing(2),
    },
}));

function Navbar() {

    const classes = useStyles();

    const [quantity, setQuantity] = useState(0);
    const [redirect, setRedirect] = useState(false);
    const [redirectUrl, setRedirectUrl] = useState(null);

    useEffect(() => {
        let tmpQuantity = 0;

        const cart = JSON.parse(localStorage.getItem('cart'));
        cart.itemsInCart.map((item) => {
            tmpQuantity += item.quantity;
        });
        setQuantity(tmpQuantity);
    }, []);

    return (
        <AppBar position="fixed" color="inherit">
            <Toolbar>
                <Typography variant="h6" className={classes.title}> Shop </Typography>

                <div className={classes.growElement} />

                <Tooltip title="Home">
                    <HomeIcon />
                </Tooltip>

                <Tooltip title="Cart">
                    <Badge className={classes.margin} badgeContent={quantity} color="secondary">
                        <ShoppingCartIcon onClick={handleOpenCart}/>
                    </Badge>
                </Tooltip>

            </Toolbar>
        </AppBar>

    )

}

export default Navbar;
